import React from 'react';

import './DragDropComponent.scss';
import Task from '../Task/Task';

interface DDState {
    tasks: { name: string, category: string, bgcolor: string }[]
}

class DragDropComponent extends React.Component<{}, DDState> {

    initState() {
        this.setState({
            tasks: [
                {name: "A", category: "wip", bgcolor: "yellow"},
                {name: "B", category: "wip", bgcolor: "pink"},
                {name: "C", category: "complete", bgcolor: "skyblue"}
            ]
        });
    }

    componentWillMount() {
        this.initState();
    }

    onDragOver = (e: any) => {
        e.preventDefault();
    }

    onDragStart = (e: any, id: string) => {
        console.log('dragstart:', id);
        e.dataTransfer.setData("id",id);
    }

    onDrop = (e: any, category: string) => {
        let id = e.dataTransfer.getData("id");
        let tasks = this.state.tasks.filter(task => {
            if (task.name == id) {
                task.category = category;
            }
            return task;
        })
    }

    render() {
        let tasks = {
            wip: [],
            complete: []
        }

        return (
            <div className="container-drag">
                <div className="wip">
                    <span className="task-header">WIP</span>
                    <div 
                        key={'A'} 
                        onDragStart={(e) => this.onDragStart(e, "A")} 
                        draggable
                        className="draggable" 
                        style={{backgroundColor: 'yellow'}}>
                    A
                    </div>
                    <div 
                        key={'B'} 
                        onDragStart={(e) => this.onDragStart(e, "B")} 
                        draggable
                        className="draggable" 
                        style={{backgroundColor: 'pink'}}>
                    B
                    </div>
                </div>
                <div className="droppable" onDragOver={(e)=>this.onDragOver(e)} onDrop={(e)=>this.onDrop(e, "complete")}>
                    <span className="task-header">COMPLETED</span>
                    <div 
                        key={'C'} 
                        onDragStart={(e) => this.onDragStart(e, "C")} 
                        draggable
                        className="draggable" 
                        style={{backgroundColor: 'skyblue'}}>
                    C
                    </div>
                </div>
            </div>
        );
    }
}

export default DragDropComponent;
