import React from 'react';

import './DragDropComponent.scss';
import Task from '../Task/Task';

interface DDState {
    tasks: { name: string, category: string, bgcolor: string }[]
}

class DragDropComponent extends React.Component<{}, DDState> {

    initState() {
        this.setState({
            tasks: [
                {name: "A", category: "wip", bgcolor: "yellow"},
                {name: "B", category: "wip", bgcolor: "pink"},
                {name: "C", category: "complete", bgcolor: "skyblue"}
            ]
        });
    }

    componentWillMount() {
        this.initState();
    }

    render() {
        let tasks = {
            wip: [],
            complete: []
        }

        this.state.tasks.forEach(t => {
            this.state.tasks[t.category].push(
                <div 
                    key={t.name} 
                    className="draggable" 
                    style={{backgroundColor: t.bgcolor}}
                > 
                </div>
            );
        });

        return (
            <div className="container-drag">

            </div>
        );
    }
}

export default DragDropComponent;
