interface Action {
    type: string;
    payload: any;
}

interface Board {
    id: number, 
    img: string, 
    name: string, 
    dueDate: string, 
    tasks: Array<Task>
}

interface Task {
    taskId: number,
    category: string,
    taskName: string,
    taskDueDate: string,
    taskStatus: string
}

interface State {
    activeBoard: number;
    boardsList: Array<Board>;
    activeBoardData: {}
}

const initialState: State = {
    activeBoard: 0,
    boardsList: [],
    activeBoardData: {}
};

const getActiveBoardData = (state: State) => {
    return state.boardsList.filter((item) => item.id === state.activeBoard);
}

const reducer = (state: State = initialState, action: Action) => {

    switch (action.type) {
        case 'SET_ACTIVE_BOARD':
            return {
                ...state,
                activeBoard: action.payload,
                activeBoardData: getActiveBoardData(state)
            }
        case 'CREATE_NEW_BOARD':
            return {
                ...state,
                activeBoard: action.payload.id,
                boardsList: [...state.boardsList, action.payload]
            }
        default:
            return state;
    }
}

export default reducer;
