import React from 'react';

import './DragDropComponent.scss';

import Task from '../Task/Task';
import Panel from '../Panel/Panel';

interface DDState {
    tasks: { key: string, panel: string, taskInfo: {} }[]
}

class DragDropComponent extends React.Component<{}, DDState> {

    todoTasks: Array<JSX.Element> = [];
    inprogressTasks: Array<JSX.Element> = [];
    doneTasks: Array<JSX.Element> = [];

    initState() {
        this.setState({
            tasks: [
                {key: '1', panel: "todo", taskInfo: {}},
                {key: '2', panel: "inprogress", taskInfo: {}},
                {key: '3', panel: "done", taskInfo: {}}
            ]
        });

        this.todoTasks.push(<Task 
            taskCategory="TD"
            taskName="TD 0"
            taskDate="03-09-2019"
            taskStatus="Active"
        />);

        this.todoTasks.push(<Task 
            taskCategory="TD"
            taskName="TD 1"
            taskDate="03-10-2019"
            taskStatus="Active"
        />);

        this.inprogressTasks.push(<Task 
            taskCategory="IP"
            taskName="IP 0"
            taskDate="03-10-2019"
            taskStatus="Active"
        />);

        this.inprogressTasks.push(<Task 
            taskCategory="IP"
            taskName="IP 1"
            taskDate="03-10-2019"
            taskStatus="Active"
        />);

        this.inprogressTasks.push(<Task 
            taskCategory="IP"
            taskName="IP 2"
            taskDate="03-10-2019"
            taskStatus="Active"
        />);

        this.doneTasks.push(<Task 
            taskCategory="DN"
            taskName="DN 0"
            taskDate="03-10-2019"
            taskStatus="Active"
        />);
    }

    componentWillMount() {
        this.initState();
    }

    onDragStart = (e: React.DragEvent, id: string) => {
        console.log('dragstart:', id);
        e.dataTransfer.setData("id",id);
    }

    onDragOver = (e: React.DragEvent) => {
        e.preventDefault();
    }

    onDrop = (e: React.DragEvent, cat: string) => {
        let id = e.dataTransfer.getData("id");
        let tasks = this.state.tasks.filter((task) => {
            if (task.key == id) {
                task.panel = cat;
            }
            return task;
        });

        this.setState({
            ...this.state,
            tasks
        });
    }

    render() {
        let tasks: {wip: Array<JSX.Element>, complete: Array<JSX.Element>} = {
            wip: [],
            complete: []
        }

        this.state.tasks.forEach((t) => {
            if(t.panel == 'wip') {
                tasks.wip.push( 
                    <div 
                        key={t.key}
                        onDragStart={(e) => this.onDragStart(e, t.key)} 
                        draggable 
                    >
                        <Task 
                            taskCategory="IT"
                            taskName="Implement redux to project"
                            taskDate="03-09-2019"
                            taskStatus="Active"
                        />
                    </div>
                    );
            } else {
                tasks.complete.push( 
                    <div 
                        key={t.key}
                        onDragStart={(e) => this.onDragStart(e, t.key)} 
                        draggable 
                    >
                        <Task 
                            taskCategory="IT"
                            taskName="Implement redux to project"
                            taskDate="03-09-2019"
                            taskStatus="Active"
                        />
                    </div>
                    );
            }

        });

        return (
            <div>
                <div className="wip" 
                    onDragOver={(e)=>this.onDragOver(e)}
                    onDrop={(e)=>{this.onDrop(e, "wip")}}>
                    <span className="task-header">WIP</span>
                    {tasks.wip}
                </div>
                <div className="droppable" 
                    onDragOver={(e)=>this.onDragOver(e)} 
                    onDrop={(e)=>this.onDrop(e, "complete")}>
                    <span className="task-header">COMPLETED</span>
                    {tasks.complete}
                </div>

                <Panel 
                    tasksList={todoTasks}
                    panelTitle="TODO"
                    panelQuantity={10}
                />
                <Panel 
                    tasksList={inprogressTasks}
                    panelTitle="IN PROGRESS"
                    panelQuantity={10}
                />
                <Panel 
                    tasksList={doneTasks}
                    panelTitle="DONE"
                    panelQuantity={10}
                />
            </div>
        );
    }
}

export default DragDropComponent;
