interface Action {
    type: string;
    payload: any;
}

interface Board {
    id: number, 
    img: string, 
    name: string, 
    dueDate: string, 
    todos: Array<Task>,
    inprogress: Array<Task>,
    done: Array<Task>
}

interface Task {
    taskId: number,
    category: string,
    taskName: string,
    taskDueDate: string,
    taskStatus: string
}

interface State {
    activeBoard: number;
    boardsList: Array<Board>;
}

const initialState: State = {
    activeBoard: 0,
    boardsList: []
};

const reducer = (state: State = initialState, action: Action) => {

    const getActiveBoardObject = (): Board => {
        let currentBoard = state.boardsList.filter((b) => b.id === state.activeBoard);
        return currentBoard[0]; 
    }

    const addTaskToActiveBoardObject = (action: Action) => {
        state.boardsList.map((b) => {
            if (b.id === state.activeBoard) {
                switch (action.payload.panel) {
                    case 'todos':
                        b.todos.push(action.payload.task);
                        break;
                    case 'inprogress':
                        b.inprogress.push(action.payload.task);
                        break;
                    case 'done':
                        b.done.push(action.payload.task);
                        break;
                    default:
                        b.todos.push(action.payload.task);
                        break;
                }
            }
        });
    }

    switch (action.type) {
        case 'SET_ACTIVE_BOARD':
            return {
                ...state,
                activeBoard: action.payload
            }

        case 'CREATE_NEW_BOARD':
            return {
                ...state,
                activeBoard: action.payload.id,
                boardsList: [...state.boardsList, action.payload]
            }
        case 'CREATE_NEW_TASK':
            addTaskToActiveBoardObject(action);
            return {
                ...state,

            }
        default:
            return state;
    }
}

export default reducer;
