import React from 'react';

import './DragDropComponent.scss';
import Task from '../Task/Task';

interface DDState {
    tasks: { name: string, category: string, bgcolor: string }[]
}

class DragDropComponent extends React.Component<{}, DDState> {

    initState() {
        this.setState({
            tasks: [
                {name: "A", category: "wip", bgcolor: "yellow"},
                {name: "B", category: "wip", bgcolor: "pink"},
                {name: "C", category: "complete", bgcolor: "skyblue"}
            ]
        });
    }

    componentWillMount() {
        this.initState();
    }

    render() {
        let tasks = {
            wip: [
                `<div key={A} className="draggable" style="yellow">A</div>`,
                `<div key={B} className="draggable" style="pink">A</div>`,
            ],
            complete: [
                `<div key={C} className="draggable" style="skyblue">A</div>`,
            ]
        }

        return (
            <div className="container-drag">
                <div className="wip">
                    <span className="task-header">WIP</span>
                    <div key={'A'} className="draggable" style={{backgroundColor: 'yellow'}}>A</div>
                    <div key={'B'} className="draggable" style={{backgroundColor: 'pink'}}>A</div>
                </div>
                <div className="droppable">
                    <span className="task-header">COMPLETED</span>
                    {tasks.complete}
                </div>
            </div>
        );
    }
}

export default DragDropComponent;
