interface Action {
    type: string;
    payload: string;
}

interface State {
    activeBoard: string
}

const initialState: State = {
    activeBoard: '1'
};

const reducer = (state: State = initialState, action: Action) => {
    switch (action.type) {
        case 'SET_ACTIVE_BOARD':
            return {
                activeBoard: action.payload
            }
        default:
            return state;
    }
}

export default reducer;
