import React from 'react';
import './App.scss';

import Sidebar from './components/Sidebar/Sidebar';
import Board from './components/Board/Board';

interface AppState {
  activeBoard: string;
}

interface AppAction {
  type: string;
  payload: string;
}

interface AppDispatch {
  dispatch: (arg: AppAction) => (AppAction)
}

class App extends React.Component<{}, AppState> {

  state: AppState = {
    activeBoard: ''
  }

  render () {
    return (
      <div className="app">
        <Sidebar /> 
        {/*<Board 
          boardName="Board "
          boardDueDate="01-10-2019"
        />;*/}
        <button onClick={onSetActiveBoard}></button>
      </div>
    );
  }

}

const mapStateToProps = (state: AppState) => {
  return {
    activeBoard: state.activeBoard
  }
}

const mapDispatchToProps = (dispatch: any) => {
  return {
    onSetActiveBoard: () => dispatch({type: 'SET_ACTIVE_BOARD', payload: '2'})
  }
};

export default App;
