import React from 'react';

import './DragDropComponent.scss';

class DragDropComponent extends React.Component {

    task: JSX.Element = (
        <div 
            key="a" 
            onDragStart={(e) => this.onDragStart(e, "a")} 
            draggable 
            className="draggable" 
            style={{backgroundColor: "red"}}> A </div>
    );

    onDragOver = (e: any) => {
        e.preventDefault();
    }

    onDragStart = (e: any, id: string) => {
        console.log('dragstart:', id);
        e.dataTransfer.setData("id",id);
    }

    onDrop = (e: any, category: string) => {
        let id = e.dataTransfer.getData("id");
        document.getElementById()
    }

    render() {

        return (
            <div className="container-drag">
                <div className="wip" onDrop={(e)=>{this.onDrop(e, "wip")}}>
                    <span className="task-header">WIP</span>
                    {this.todoTask}
                </div>
                <div className="droppable" onDragOver={(e)=>this.onDragOver(e)} onDrop={(e)=>this.onDrop(e, "complete")}>
                    <span className="task-header">COMPLETED</span>
                    {this.completeTask}
                </div>
            </div>
        );
    }
}

export default DragDropComponent;
