interface Action {
    type: string;
    payload: any;
}

interface Board {
    id: number, 
    img: string, 
    name: string, 
    dueDate: string, 
    todos: Array<Task>,
    inprogress: Array<Task>,
    done: Array<Task>
}

interface Task {
    taskId: number,
    category: string,
    taskName: string,
    taskDueDate: string,
    taskStatus: string
}

interface State {
    activeBoard: number;
    boardsList: Array<Board>;
    board: {}
}

const initialState: State = {
    activeBoard: 0,
    boardsList: [],
    board: {}
};

const reducer = (state: State = initialState, action: Action) => {

    switch (action.type) {
        case 'SET_ACTIVE_BOARD':

            return {
                ...state,
                activeBoard: action.payload
            }
        case 'CREATE_NEW_BOARD':
            return {
                ...state,
                activeBoard: action.payload.id,
                boardsList: [...state.boardsList, action.payload]
            }
        default:
            return state;
    }
}

export default reducer;
